import React, { useState } from 'react';
import './AirportInfo.css';
import Flights from './Flights';

function AirportInfo() { 
    const [flights,setFlights] = useState([])
    const [places, setPlaces] = useState([])
    const [origin, setOrigin] = useState([])
    const [query, setQuery] = useState("")
    const [showFlights,setShowFlights] = useState(false)

    function handleSubmit(e) {
        e.preventDefault()
        async function fetchMyAPI() {
            const reqOptions = {
                method: 'GET',
                headers: {
                    "x-rapidapi-key": "9c838d17b0msh9d78bbfa4f324fbp109103jsn16509938ea2e",
                    "x-rapidapi-host": "skyscanner-skyscanner-flight-search-v1.p.rapidapi.com",
                    "useQueryString": true
                }
            }

            // get and set the origin
            let originResponse = await fetch("https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/autosuggest/v1.0/US/USD/en-US/?" + new URLSearchParams({query: query}), reqOptions)
            originResponse = await originResponse.json()
            setOrigin(originResponse.Places)
            let originPlaceId = origin[0].PlaceId

            let response = await fetch("https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browsedates/v1.0/US/USD/en-US/SFO-sky/" + originPlaceId + "/2021-03-20?inboundpartialdate=2021-03-25", reqOptions)
            response = await response.json()
            console.log(response.Quotes)
            setFlights(response.Quotes)
            setPlaces(response.Places)
        }
        fetchMyAPI()
        setShowFlights(true)
        setQuery("")
    }

    return(
        <div className="airportinfo">
           <form onSubmit={handleSubmit}>
                <label htmlFor="queryInput">Origin:</label>
                <input id="queryInput" value={query} onChange={e => setQuery(e.target.value)} required/>
                <button className="search">Submit</button>
           </form>
           { showFlights ? <Flights flights={flights} places={places}></Flights> : <></>}
        </div>
    )
}

export default AirportInfo;
