import React, { useState } from 'react';
import './AirportInfo.css';
import Flights from './Flights';

function AirportInfo() { 
    const [flights,setFlights] = useState([])
    const [places, setPlaces] = useState([])
    const [query, setQuery] = useState("")
    const [destQuery, setDestQuery] = useState("")
    const [showFlights, setShowFlights] = useState(false)

    function handleSubmit(e) {
        e.preventDefault()
        async function fetchMyAPI() {
            const reqOptions = {
                method: 'GET',
                headers: {
                    "x-rapidapi-key": "9c838d17b0msh9d78bbfa4f324fbp109103jsn16509938ea2e",
                    "x-rapidapi-host": "skyscanner-skyscanner-flight-search-v1.p.rapidapi.com",
                    "useQueryString": true
                }
            }

            let responseOrigin = await fetch("https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/autosuggest/v1.0/US/USD/en-US/?" + new URLSearchParams({query: query}), reqOptions)
            responseOrigin = await responseOrigin.json()
            let originId = responseOrigin.Places[0].PlaceId
            console.log(originId)

            let responseDestination = await fetch("https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/autosuggest/v1.0/US/USD/en-US/?" + new URLSearchParams({query: destQuery}), reqOptions)
            responseDestination = await responseDestination.json()
            let destinationId = responseDestination.Places[0].PlaceId
            console.log(destinationId)

            let response = await fetch("https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browsedates/v1.0/US/USD/en-US/" + originId + "/LAX-sky/2021-03-20?inboundpartialdate=2021-03-25", reqOptions)
            response = await response.json()
            setFlights(response.Quotes)
            setPlaces(response.Places)
        }
        fetchMyAPI()
        setShowFlights(true)
        setQuery("")
        setDestQuery("")
    }

    return(
        <div className="airportinfo">
           <form onSubmit={handleSubmit}>
                <label htmlFor="queryInput">Origin:</label>
                <input id="queryInput" value={query} onChange={e => setQuery(e.target.value)} required/>
                <label htmlFor="destQueryInput">Origin:</label>
                <input id="destQueryInput" value={destQuery} onChange={e => setDestQuery(e.target.value)} required/>
                <button className="search">Submit</button>
                <label for="currency">Currency:</label>

           </form>
           { showFlights ? <Flights flights={flights} places={places}></Flights> : <></>}
        </div>
    )
}

export default AirportInfo;
